import { useState } from "react";

function Domin() {
  const [email, setEmail] = useState("");
  const [loading, setLoading] = useState(false);
  const [data, setData] = useState(null);
  const [error, setError] = useState("");

  const handleSearch = async (e) => {
    e.preventDefault(); 

    if (!email) {
      setError("Please enter email or domain");
      return;
    }

    setLoading(true);
    setError("");
    setData(null);

    try {
      const response = await fetch("http://localhost:5000/api/domain", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({
          user_input: email
        })
      });

      const result = await response.json();
      setData(result);

    } catch (err) {
      setError("Something went wrong");
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <form onSubmit={handleSearch}>
        <h1>Check Domain</h1>

        <div className="search-box">
          <input
            type="text"
            placeholder="Enter your email or domain"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />

          <button type="submit" disabled={loading}>
            {loading ? "Checking..." : "Search"}
          </button>
        </div>
      </form>

{error && <p style={{ color: "red" }}>{error}</p>}
{data && (
  <div style={{ marginTop: "20px" }}>
    <h3>Domain Report</h3>

    <p><b>CDS Count:</b> {data.cds_count}</p>
    <p><b>UB Count:</b> {data.ub_count}</p>
    <p><b>CL Count:</b> {data.cl_count}</p>
    <p><b>CB Count:</b> {data.cb_count}</p>

    {/* Show leaked data if available */}
    {data.cl_data && data.cl_data.length > 0 && (
      <>
        <h4>Leaked Records</h4>

        {data.cl_data.map((item, index) => (
          <div key={index} style={{ marginBottom: "10px" }}>
            <p><b>Domain:</b> {item.domain}</p>
            <p><b>Email:</b> {item.email}</p>
            <p><b>Leaked Date:</b> {item.leaked_date}</p>
            <p><b>Leaked From:</b> {item.leaked_from}</p>
          </div>
        ))}
      </>
    )}
  </div>
)}
 

    </>
  );
}

export default Domin;
