import { useState } from "react";
import axios from "axios";

function App() {
  const [numer, setNumber] = useState("");
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false);

  const checkMobile = async () => {
    if (!number) {
      setMessage("Please enter mobile number");
      return;
    }

    try {
      setLoading(true);
      setMessage("");

      const res = await axios.post(
        "http://localhost:5000/api/check-mobile",
        { number }
      );

      if (res.data.success) {
        setMessage("✅ Account Found");
      } else {
        setMessage("❌ Account Not Found");
      }

    } catch (error) {
      setMessage("Server error");
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <h2>Check Account</h2>

      <input
        type="text"
        placeholder="Enter mobile number"
        value={number}
        onChange={(e) => setNumber(e.target.value)}
       />

      <button onClick={checkMobile} style={styles.button}>
        {loading ? "Checking..." : "Check"}
      </button>

      {message && <p>{message}</p>}
    </>
  );
}

 

export default App;
