import { useState } from "react";

function App() {
  const [html, setHtml] = useState("");
  const [number, setNumber] = useState("");
  const [result, setResult] = useState("");

  // ðŸ”¹ Load Amazon HTML
  const loadHtml = async () => {
    const res = await fetch("http://localhost:5000/api/account", {
      method: "POST"
    });
    const data = await res.json();
    setHtml(data.html);
  };

  // ðŸ”¹ Check account (SIMULATED)
  const checkAccount = async () => {
    const res = await fetch("http://localhost:5000/api/check-account", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ number })
    });

    const data = await res.json();
    setResult(data.message);
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>Amazon Sign In (Demo)</h2>

      <button onClick={loadHtml}>Load Amazon HTML</button>

      <div
        style={{ border: "1px solid #ccc", marginTop: 20, padding: 10 }}
        dangerouslySetInnerHTML={{ __html: html }}
      />

      <hr />

      <input
        type="text"
        placeholder="Enter Mobile Number"
        value={number}
        onChange={(e) => setNumber(e.target.value)}
      />

      <button onClick={checkAccount}>Check Account</button>

      <h3>{result}</h3>
    </div>
  );
}

export default App;
