import { useState } from "react";
import axios from "axios";

function DomainReportTable() {
  const [input, setInput] = useState("");
  const [loading, setLoading] = useState(false);
  const [data, setData] = useState(null);
  const [error, setError] = useState("");

  const handleSearch = async () => {
    if (!input) {
      setError("Please enter domain or email");
      return;
    }

    setLoading(true);
    setError("");
    setData(null);

    try {
      const res = await axios.post("http://localhost:5000/api/domain", {
        user_input: input,
      });

      setData(res.data);
    } catch (err) {
      setError("Failed to fetch report");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Domain Leak Report</h2>

      <input
        type="text"
        placeholder="Enter domain or email"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        style={{ padding: "8px", width: "260px" }}
      />

      <button onClick={handleSearch} style={{ marginLeft: "10px" }}>
        Search
      </button>

      {loading && <p>Loading...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}

      {/* ✅ TABLE */}
      {data?.success && data?.credentials?.cl_count > 0 && (
        <table
          border="1"
          cellPadding="10"
          cellSpacing="0"
          style={{ marginTop: "20px", width: "100%", textAlign: "left" }}
        >
          <thead style={{ background: "#f2f2f2" }}>
            <tr>
              <th>#</th>
              <th>Email</th>
              <th>Password</th>
              <th>Leaked Date</th>
            </tr>
          </thead>
          <tbody>
            {data.credentials.cl_data.map((item, index) => (
              <tr key={index}>
                <td>{index + 1}</td>
                <td>{item.email}</td>
                <td>{item.password}</td>
                <td>{item.leaked_date}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {/* ❌ No Data */}
      {data?.success && data?.credentials?.cl_count === 0 && (
        <p style={{ marginTop: "20px" }}>No leaked data found ✅</p>
      )}
    </div>
  );
}

export default DomainReportTable;
