import { useState } from "react";

function Domin() {
  const [email, setEmail] = useState("");
  const [loading, setLoading] = useState(false);
  const [data, setData] = useState(null);
  const [error, setError] = useState("");

  // ðŸ”¥ HTML â†’ TEXT decoder
  const htmlToText = (html) => {
    if (!html) return "";
    const doc = new DOMParser().parseFromString(html, "text/html");
    return doc.body.textContent || "";
  };

  const handleSearch = async (e) => {
    e.preventDefault();

    if (!email) {
      setError("Please enter email or domain");
      return;
    }

    setLoading(true);
    setError("");
    setData(null);

    try {
      const response = await fetch("http://localhost:5000/api/domain", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          user_input: email,
        }),
      });

      const result = await response.json();

      if (!result.success) {
        setError(result.message || "No data found");
      } else {
        setData(result.result);
      }
    } catch (err) {
      setError("Something went wrong");
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <form onSubmit={handleSearch}>
        <h1>Check Domain / Email</h1>

        <input
          type="text"
          placeholder="Enter email or domain"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />

        <button type="submit" disabled={loading}>
          {loading ? "Checking..." : "Search"}
        </button>
      </form>

      {error && <p style={{ color: "red" }}>{error}</p>}

      {/* âœ… TEXT OUTPUT (HTML DECODED) */}
      {data && (
        <div style={{ marginTop: "20px" }}>
          <h3>Result</h3>

          {Object.entries(data).map(([key, value]) => (
            <p key={key}>
              <b>{key}:</b>{" "}
              {typeof value === "string"
                ? htmlToText(value)
                : String(value)}
            </p>
          ))}
        </div>
      )}
    </>
  );
}

export default Domin;
