import { useState } from "react";
function StudentDetils() {
  const [enroll, setEnroll] = useState("");
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [name, setName] = useState("");
  const [users, setUsers] = useState([]);
  const [message,setmassage] =useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!upi.includes("@")) {
      alert("Please enter valid UPI ID");
      return;
    }

    setLoading(true);
    setError("");
    setData(null);
    setmassage(true);

    try {
      const response = await fetch("http://localhost:5000/api/send", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          digital_payment_id:  enroll, // ✅ FIXED
          consent: "Y",
          consent_text:
            "We confirm obtaining valid customer consent to access/process their digital payment id data. Consent remains valid, informed, and unwithdrawn.",
        }),
      });

      const result = await response.json();
      setData(result);
      
      
    } catch (err) {
      setError("API call failed");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2> Student Details</h2>
      
      {/* ✅ API MESSAGE */}
      {data?.message && <p><b>{data.message}</b></p>}

      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Enter UPI ID "
          value={enroll}
          onChange={(e) =>  setEnroll(e.target.value)}
          style={{ width: "300px", padding: "8px" }}
        />

        <br /><br />
        <button type="submit">Fetch Data</button>
      </form>

      {loading && <p>Loading...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}

      {data?.newUser && (
        <>
           <h3>New User Details</h3>
          
          <p>Name: {data. student.name || "N/A"}</p>
          <p>Bank: {data.newUser.colla || "N/A"}</p>
          <p>City: {data.newUser.city || "N/A"}</p>
          <p>State: {data.newUser.state || "N/A"}</p>
        </>
       
      )} 
    </div>
  );
}

export default  StudentDetils;
