import { useState } from "react";

function Domin() {
  const [email, setEmail] = useState("");
  const [loading, setLoading] = useState(false);
  const [data, setData] = useState(null);
  const [error, setError] = useState("");

  const handleSearch = async (e) => {
    e.preventDefault(); 

    if (!email) {
      setError("Please enter email or domain");
      return;
    }

    setLoading(true);
    setError("");
    setData(null);

    try {
      const response = await fetch("http://localhost:5000/api/domain", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({
          user_input: email
        })
      });

      const result = await response.json();
      setData(result);

    } catch (err) {
      setError("Something went wrong");
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <form onSubmit={handleSearch}>
        <h1>Check Domain</h1>

        <div className="search-box">
          <input
            type="text"
            placeholder="Enter your email or domain"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />

          <button type="submit" disabled={loading}>
            {loading ? "Checking..." : "Search"}
          </button>
        </div>
      </form>

      {error && <p style={{ color: "red" }}>{error}</p>}{data && typeof data === "object" && (
  <div style={{ marginTop: "20px" }}>
    <h3>Result</h3>

    {Object.entries(data).map(([key, value]) => (
      <div key={key} style={{ marginBottom: "12px" }}>
        <b>{key}:</b>

        {/* NUMBER / STRING */}
        {typeof value !== "object" && (
          <span> {String(value)}</span>
        )}

        {/* ARRAY */}
        {Array.isArray(value) && (
          <ul>
            {value.map((item, index) => (
              <li key={index}>
                {typeof item === "object"
                  ? Object.entries(item).map(([k, v]) => (
                      <span key={k}>
                        <b>{k}</b>: {String(v)}{" "}
                      </span>
                    ))
                  : String(item)}
              </li>
            ))}
          </ul>
        )}

        {/* OBJECT */}
        {!Array.isArray(value) && typeof value === "object" && (
          <ul>
            {Object.entries(value).map(([k, v]) => (
              <li key={k}>
                <b>{k}:</b> {String(v)}
              </li>
            ))}
          </ul>
        )}
      </div>
    ))}
  </div>
)}
 
    </>
  );
}

export default Domin;
