import { useState } from "react";

function Domin() {
  const [domin_id, setDomin_id] = useState("");
  const [loading, setLoading] = useState(false);
  const [data, setData] = useState(null);
  const [error, setError] = useState("");

  const handleSearch = async (e) => {
    e.preventDefault();

    if (!input) {
      setError("Please enter domain or email");
      return;
    }

    setLoading(true);
    setError("");
    setData(null);

    try {
      const response = await fetch("http://localhost:5000/api/domain", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          user_input: domin_id,
        }),
      });

      const result = await response.json();
      setData(result); // ✅ store SAME response

    } catch (err) {
      setError("Something went wrong");
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <h1>Check Domain / Email</h1>

      <form onSubmit={handleSearch}>
        <input
          type="text"
          placeholder="Enter domain or email"
          value={ domin_id}
          onChange={(e) => SsetDomin_id(e.target.value)}
        />

        <button type="submit" disabled={loading}>
          {loading ? "Checking..." : "Search"}
        </button>
      </form>

      {error && <p style={{ color: "red" }}>{error}</p>}

      {/* ✅ SHOW RESPONSE EXACTLY */}
      {data && (
        <div style={{ marginTop: "20px" }}>
          <h3>Response</h3>

          <textarea
            rows="20"
            style={{ width: "100%" }}
            readOnly
            value={JSON.stringify(data, null, 2)}
          />
        </div>
      )}
    </>
  );
}

export default Domin;
