import { useState } from "react";
 import "./App.css";

function ApiAccount() {
  const [email, setEmail] = useState("");
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");

  const handleSearch = async () => {
    if (!email) {
      setError("Please enter mobile number");
      return;
    }

    setLoading(true);
    setError("");
    setMessage("");

    try {
      const response = await fetch("http://localhost:5000/api/account",{
        method: "POST",
        headers: {
          "Cookie":"aws-waf-token=bfe89cfc-7cf6-4287-8d9f-403e7ff253f6:BgoAecMwTRAVAAAA:tDyVdttso0mWCbJg4aSbnCR9qY+LSQuakhwl+NmhFA/5dIcE9iuxYDv9/dbGm0tbQEe453Fa+EKSX4v6XONoisGC7v7I6FE8FJDoBb/Y3ARUeLvz4zF4SbciUeXYZbAbBUImPBTKVWQx0WsLbLcZtvcGQJhoDoM9wXrYGFPeSGSlgKOXmopER7Jm8KMy7Hj0; session-id=521-5211162-4996064; i18n-prefs=INR; lc-acbin=en_IN; ubid-acbin=524-0189686-7251238; session-id-time=2082787201l; session-; id_pkel=n1; csm-hit=tb:JYRMP5N1M6ZMM8MECYWQ+b-EFV6VPZA25ZKVJMRYQYD|1769703763304&t:1769703763304&adb:adblk_no; rxc=AJhcJRCqcOgHJuzxa0s; id_pk=eyJuIjoiMSIsImFmIjoiMSIsImNjIjoiMSJ9",
        },
         body: JSON.stringify({
          digital_payment_id: upi, // ✅ FIXED
          consent: "Y",
          consent_text:
            "We confirm obtaining valid customer consent to access/process their digital payment id data. Consent remains valid, informed, and unwithdrawn.",
        }),
      }
      );
      const result = await response.json;

      // ✅ CORRECT ACCESS
      if (result) {
        setMessage("✅ Account Found");
      } else {
        setMessage("❌ Account Not Found");
      }

    } catch (err) {
        setError("Something went wrong");
        
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="app" style={{ padding: "20px" }}>
      <h1>Check Account</h1>

      <div className="search-box">
        <input
          type="number"
          placeholder="Enter mobile number"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <button onClick={handleSearch}>Search</button>
      </div>

      {loading && <p>Loading...</p>}
      {error && <p className="error">{error}</p>}
      {message && <p>{message}</p>}
    </div>
  );
}

export default ApiAccount;
