import { useState } from "react";

function Domin() {
  const [domin_id, setDomin_id] = useState("");
  const [loading, setLoading] = useState(false);
  const [data, setData] = useState(null);
  const [error, setError] = useState("");

  const handleSearch = async (e) => {
    e.preventDefault();

    if (!domin_id) {
      setError("Please enter domain or email");
      return;
    }

    setLoading(true);
    setError("");
    setData(null);

    try {
      const response = await fetch("http://localhost:5000/api/domain", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          user_input: domin_id,
        }),
      });

      const result = await response.json();
      setData(result); // ✅ store SAME response

    } catch (err) {
      setError("Something went wrong");
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <h1>Check Domain / Email</h1>

      <form onSubmit={handleSearch}>
        <input
          type="text"
          placeholder="Enter domain or email"
          value={ domin_id}
          onChange={(e) => setDomin_id(e.target.value)}
        />

        <button type="submit" disabled={loading}>
          {loading ? "Checking..." : "Search"}
        </button>
      </form>

      {error && <p style={{ color: "red" }}>{error}</p>}
  
      {data?.success && data?.credentials?.cl_count > 0 && (
        <table
          border="1"
          cellPadding="10"
          cellSpacing="0"
          style={{ marginTop: "20px", width: "100%", textAlign: "left" }}
        >
          <thead style={{ background: "#f2f2f2" }}>
            <tr>
              <th>#</th>
              <th>dog fight</th>
              <th>Password</th>
              <th>Leaked Date</th>
            </tr>
          </thead>
          <tbody>
            {data.credentials.cl_data.map((item, index) => (
              <tr key={index}>
                <td>{index + 1}</td>
                <td>{item.email}</td>
                <td>{item.password}</td>
                <td>{item.leaked_date}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </>
  );
}

export default Domin;
