import { useState } from "react";

function useDomainReport() {
  const [loading, setLoading] = useState(false);
  const [report, setReport] = useState(null);
  const [error, setError] = useState(null);

  const fetchDomainReport = async (domain) => {
    if (!domain) {
      setError("Domain is required");
      return;
    }

    setLoading(true);
    setError(null);
    setReport(null);

    try {
      const response = await fetch("http://localhost:5000/api/domain", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({
          user_input: domain
        })
      });

      const data = await response.json();

      if (!data.success) {
        throw new Error(data.message);
      }

      // ✅ ONLY domain report stored
      setReport(data.report);

    } catch (err) {
      setError(err.message || "Failed to fetch domain report");
    } finally {
      setLoading(false);
    }
  };

  return {
    loading,
    report,
    error,
    fetchDomainReport
  };
}

export default useDomainReport;
