import { useState } from "react";

function Domin() {
  const [domin_id, setDomin_id] = useState("");
  const [loading, setLoading] = useState(false);
  const [data, setData] = useState(null);
  const [error, setError] = useState("");

  const handleSearch = async (e) => {
    e.preventDefault();

    if (!domin_id) {
      setError("Please enter domain or email");
      return;
    }

    setLoading(true);
    setError("");
    setData(null);

    try {
      const response = await fetch("http://localhost:5000/api/domain", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          user_input: domin_id,
        }),
      });

      const result = await response.json();
      setData(result.result); // ✅ store the "result" object directly

    } catch (err) {
      setError("Something went wrong");
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <h1>Check Domain / Email</h1>

      <form onSubmit={handleSearch}>
        <input
          type="text"
          placeholder="Enter domain or email"
          value={domin_id}
          onChange={(e) => setDomin_id(e.target.value)}
        />

        <button type="submit" disabled={loading}>
          {loading ? "Checking..." : "Search"}
        </button>
      </form>

      {error && <p style={{ color: "red" }}>{error}</p>}

      {/* ✅ SHOW RESPONSE IN READABLE FORMAT */}
      {data && (
        <div style={{ marginTop: "20px" }}>
          <h3>Leaked Accounts</h3>

          {data.cl_count > 0 ? (
            data.cl_data.map((item, index) => (
              <div
                key={index}
                style={{
                  border: "1px solid #ccc",
                  padding: "10px",
                  marginBottom: "10px",
                }}
                >
                <p> Domain:  {item.domain}</p>
                <p> Email: {itemmail}</p>
                <p> Password: {item.password}</p>
                <p>Leaked Date:  {item.leaked_date}</p>
                <p>Leaked From: {item.leaked_from}</p>
              </div>
            ))
          ) : (
            <p>No leaked accounts found.</p>
          )}
        </div>
      )}
    </>
  );
}

export default Domin;
