import { useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [query, setQuery] = useState("");
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault(); // built-in prevent reload

    if (!query.trim()) {
      setError("Please enter search text");
      return;
    }

    setLoading(true);
    setError("");
    setResults([]);

    try {
      const res = await axios.post("http://localhost:5000/api/search", {
        query,
      });

      setResults(res.data.results); // built-in JS handling
    } catch (err) {
      setError("Server error");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="app">
      <h1>üîç Google Search</h1>

      <form className="search-box" onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Search anything..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
        <button type="submit">Search</button>
      </form>

      {loading && <p>Loading...</p>}
      {error && <p className="error">{error}</p>}

      <div className="results">
        {results.length === 0 && !loading && (
          <p style={{ color: "#777" }}>No results</p>
        )}

        {results.map((item, index) => (
          <div key={index} className="card">
            <a href={item.link} target="_blank" rel="noreferrer">
              <h3>{item.title}</h3>
            </a>
            <p>{item.snippet}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
